<script>
  document.addEventListener("DOMContentLoaded", function() {
    const nextButton = document.querySelector(".next-button");
    const sections = document.querySelectorAll(".sectionOurLove__year");
    const scrollContainer = document.querySelector(".sectionOurLove__scroll");
    const images = document.querySelectorAll(".sectionOurLove__img img");
    let currentIndex = 0;
    let isAnimating = false;

    nextButton.addEventListener("click", function(event) {
      event.preventDefault();
      
      if (isAnimating) return; // Evitar múltiples clics mientras se está animando
      isAnimating = true;

      currentIndex++;
      if (currentIndex < sections.length) {
        const targetElement = sections[currentIndex];
        scrollToElement(targetElement);
        showImage(currentIndex);
      } else {
        currentIndex = 0; // Volver al principio
        const targetElement = sections[currentIndex];
        scrollToElement(targetElement);
        showImage(currentIndex);
      }
    });

    function scrollToElement(element) {
      const containerTop = scrollContainer.getBoundingClientRect().top;
      const elementTop = element.getBoundingClientRect().top;
      const targetOffset = elementTop - containerTop + scrollContainer.scrollTop;

      scrollContainer.scrollTo({
        top: targetOffset,
        behavior: "smooth"
      });

      // Esperar a que termine la animación de desplazamiento
      setTimeout(() => {
        isAnimating = false;
      }, 800); // Ajustar este valor según la duración de tu animación CSS
    }

    function showImage(index) {
      images.forEach((img, imgIndex) => {
        img.style.display = imgIndex === index ? 'block' : 'none';
      });
    }

    // Mostrar la imagen inicial
    showImage(currentIndex);
  });

</script>

