<script>
document.addEventListener("DOMContentLoaded", function() {
    // Función para activar el primer contenido de cada tab al cargar la página
    function activateInitialContent() {
        const tabContents = document.querySelectorAll(".tab-content");
        tabContents.forEach(tabContent => {
            const firstMemberContent = tabContent.querySelector(".member-content");
            if (firstMemberContent) {
                firstMemberContent.classList.add("active");
            }
        });
    }

    // Función para manejar el clic en los nombres de los miembros
    function handleMemberClick() {
        const members = document.querySelectorAll(".member-name");
        members.forEach(member => {
            member.addEventListener("click", function() {
                const contentId = this.getAttribute("data-tab");
                const content = document.getElementById(contentId);
                const tabContent = content.closest(".tab-content");
                const allContents = tabContent.querySelectorAll(".member-content");
                allContents.forEach(content => content.classList.remove("active"));
                content.classList.add("active");
            });
        });
    }

    // Inicialmente ocultar todos los contenidos de miembros
    const allContents = document.querySelectorAll(".member-content");
    allContents.forEach(content => content.classList.remove("active"));

    // Activar el primer contenido de cada tab al cargar la página
    activateInitialContent();

    // Manejar el clic en los nombres de los miembros
    handleMemberClick();
});
</script>



